mapper.select:
select A.METHOD_ID, A.CASE_ID, A.CASE_DES, A.CASE_DATA, A.CASE_ASSERT_TYPE, A.CASE_ASSERT_VALUE, A.IS_RUN, A.IS_DEL from TEST_METHOD B,TEST_CASE A
A.IS_DEL = 0 AND B.METHOD_ID = A.METHOD_ID
AND B.SERVICE_ID = #{service_id,jdbcType=INTEGER}

		select CASE_DATA from TEST_CASE


   				METHOD_ID = #{method_id,jdbcType=INTEGER}

   			and IS_DEL = 0

		select CASE_DATA from TEST_CASE


   				CASE_ID = #{case_id,jdbcType=INTEGER}

   			and IS_DEL = 0

mapper.update:
		update TEST_CASE
        set IS_DEL = 1
        where CASE_ID = #{case_id,jdbcType=INTEGER}
